function computeWindowedRenderLimits(
  props: {
    data: any,
    getItemCount: (data: any) => number,
    maxToRenderPerBatch: number,
    windowSize: number,
    ...
  },
  prev: {
    first: number,
    last: number,
    ...
  },
  getFrameMetricsApprox: (
    index: number,
  ) => {
    length: number,
    offset: number,
    ...
  },
  scrollMetrics: {
    dt: number,
    offset: number,
    velocity: number,
    visibleLength: number,
    ...
