    key: string,
    callback: SourceCallback,
  ): {remove: () => void, ...} {
    return this._addSource(key, callback, BugReporting._fileSources);
  }

  static _addSource(
    key: string,
    callback: SourceCallback,
    source: Map<string, SourceCallback>,
